# -*- coding: utf-8 -*-
from PIL import Image, ImageDraw, ImageFontimport sqlite3stationList = []img = Image.new( 'RGB',( 2200, 2200 ), ( 255, 255, 255 ) )DBNAME = "stationData.sqlite"conn = sqlite3.connect( DBNAME )conn.text_factory = strconn.row_factory = sqlite3.Rowcur = conn.cursor()cur.execute( "SELECT STATION_NAME, LON, LAT FROM STATIONDATA" )stationList = [ ( idx[ 0 ], idx[ 1 ], idx[ 2 ] ) for idx in cur ]print(stationList[0])font = ImageFont.truetype( "MSMINCHO.ttf", 10, encoding='utf-8' )#font = ImageFont.load_default()draw = ImageDraw.Draw( img )for i in range( len( stationList ) ):	n = stationList[ i ][ 0 ]											# 駅名	x = round( ( stationList[ i ][ 1 ] - 127.652214 ) * 100, 0 ) + 100	# 緯度	y = 2000 - round( ( stationList[ i ][ 2 ] - 26.193289 ) * 100, 0 )	# 経度	draw.text( ( x, y ), unicode( n, "utf-8" ), fill = ( 0, 0, 0 ) )img.save( 'station.png', 'PNG' )